import React, { useEffect, useState } from 'react';
import { Box, Heading, Text, VStack, useMediaQuery } from '@chakra-ui/react';

const ScrollRevealComponent = () => {
  const [sectionVisible, setSectionVisible] = useState({
    section1: false,
    section2: false,
    section3: false,
  });

  const [isLargerThan768] = useMediaQuery("(min-width: 768px)");

  useEffect(() => {
    const handleScroll = () => {
      const section1 = document.getElementById('section1');
      const section2 = document.getElementById('section2');
      const section3 = document.getElementById('section3');

      const section1Top = section1.getBoundingClientRect().top;
      const section2Top = section2.getBoundingClientRect().top;
      const section3Top = section3.getBoundingClientRect().top;

      const windowHeight = window.innerHeight;

      if (section1Top < windowHeight && !sectionVisible.section1) {
        setSectionVisible(prev => ({ ...prev, section1: true }));
      }
      if (section1Top < windowHeight && sectionVisible.section1 && section2Top < windowHeight && !sectionVisible.section2) {
        setSectionVisible(prev => ({ ...prev, section2: true }));
      }
      if (section1Top < windowHeight && sectionVisible.section1 && section2Top < windowHeight && sectionVisible.section2 && section3Top < windowHeight && !sectionVisible.section3) {
        setSectionVisible(prev => ({ ...prev, section3: true }));
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [sectionVisible]);

  return (
    <VStack spacing={8} p={4}>
      <Box
        id="section1"
        opacity={sectionVisible.section1 ? 1 : 0}
        transition="opacity 0.5s"
        bg="teal.500"
        p={8}
        rounded="md"
        shadow="md"
        w={isLargerThan768 ? "50%" : "90%"}
      >
        <Heading as="h2" size="lg" mb={4} color="white">
          Section 1
        </Heading>
        <Text color="white">
          This is the first section. It will appear as you scroll down.
        </Text>
      </Box>

      <Box
        id="section2"
        opacity={sectionVisible.section2 ? 1 : 0}
        transition="opacity 0.5s"
        bg="blue.500"
        p={8}
        rounded="md"
        shadow="md"
        w={isLargerThan768 ? "50%" : "90%"}
      >
        <Heading as="h2" size="lg" mb={4} color="white">
          Section 2
        </Heading>
        <Text color="white">
          This is the second section. It will appear as you scroll down.
        </Text>
      </Box>

      <Box
        id="section3"
        opacity={sectionVisible.section3 ? 1 : 0}
        transition="opacity 0.5s"
        bg="purple.500"
        p={8}
        rounded="md"
        shadow="md"
        w={isLargerThan768 ? "50%" : "90%"}
      >
        <Heading as="h2" size="lg" mb={4} color="white">
          Section 3
        </Heading>
        <Text color="white">
          This is the third section. It will appear as you scroll down.
        </Text>
      </Box>
    </VStack>
  );
};

export default ScrollRevealComponent;
