// CalendarComponent.js
import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './CalendarComponent.css';

const localizer = momentLocalizer(moment);

const events = [
  {
    title: 'Induction + Team Building',
    start: new Date(2024, 5, 9, 9, 0), // Month is 0-based, June is 5
    end: new Date(2024, 5, 9, 12, 0),
    desc: 'Introduction and ice-breaking activities\nTeam-building exercises to foster collaboration'
  },
  {
    title: 'Familiarize with Git, GitHub, Figma',
    start: new Date(2024, 5, 16, 9, 0),
    end: new Date(2024, 5, 16, 12, 0),
    desc: 'Introduction to version control using Git and GitHub\nOverview of Figma for design collaboration\nTask: Complete a beginner\'s tutorial on GitHub and create a sample project in Figma'
  },
  {
    title: 'Researching Projects and Domains',
    start: new Date(2024, 5, 23, 9, 0),
    end: new Date(2024, 5, 23, 12, 0),
    desc: 'Strategies for effective project and domain research\nTask: Conduct a literature review on your domain of interest'
  },
  {
    title: 'Group Formation and UX/UI Session',
    start: new Date(2024, 5, 30, 9, 0),
    end: new Date(2024, 5, 30, 12, 0),
    desc: 'Form groups based on domain interests\nSession on wireframes, UX/UI principles\nDiscuss potential projects and team-building within groups\nTask: Draft the first version of your project wireframe'
  },
  {
    title: 'Product-Market Fit and Product Development',
    start: new Date(2024, 6, 7, 9, 0),
    end: new Date(2024, 6, 7, 12, 0),
    desc: 'Discussion on product-market fit and key product development considerations\nTask: Identify target users and create a preliminary product-market fit analysis'
  },
  {
    title: 'Meeting with Development Mentor',
    start: new Date(2024, 6, 14, 9, 0),
    end: new Date(2024, 6, 14, 12, 0),
    desc: 'Initial meeting with a development mentor\nTask: Begin coding your project'
  },
  {
    title: 'Mentor Session + Resume Building',
    start: new Date(2024, 6, 21, 9, 0),
    end: new Date(2024, 6, 21, 12, 0),
    desc: 'Continue meeting with development mentor\nResume building workshop\nTask: Progress in coding and review your resume'
  },
  {
    title: 'Mentor Session + LinkedIn Building',
    start: new Date(2024, 6, 28, 9, 0),
    end: new Date(2024, 6, 28, 12, 0),
    desc: 'Further guidance from development mentor\nLinkedIn profile optimization session\nTask: Continue coding and update your LinkedIn profile'
  },
  {
    title: 'Finalizing Project and Deployment',
    start: new Date(2024, 7, 4, 9, 0),
    end: new Date(2024, 7, 4, 12, 0),
    desc: 'Final mentor meeting to finalize the project and plan deployment\nStart working on project presentation\nTask: Prepare for project deployment and begin creating your presentation'
  },
  {
    title: 'Project Showcasing',
    start: new Date(2024, 7, 11, 9, 0),
    end: new Date(2024, 7, 11, 12, 0),
    desc: 'Showcase completed projects\nPresentations and feedback session\nCelebrate achievements and wrap-up'
  }
];

const CalendarComponent = () => {
  return (
    <div style={{ height: '100vh' }}>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 700, margin: '50px' }}
        tooltipAccessor="desc"
        components={{
          event: Event
        }}
        eventPropGetter={eventStyleGetter}
      />
    </div>
  );
};

const Event = ({ event }) => {
  return (
    <span>
      <strong>{event.title}</strong>
      <p>{event.desc.split('\n').map((line, index) => <span key={index}>{line}<br/></span>)}</p>
    </span>
  );
};

const eventStyleGetter = (event, start, end, isSelected) => {
  let backgroundColor = '#3174ad';
  let style = {
    backgroundColor: backgroundColor,
    borderRadius: '5px',
    opacity: 0.8,
    color: 'white',
    border: '0px',
    display: 'block'
  };
  return {
    style: style
  };
};

export default CalendarComponent;
