import React from 'react';
import { Box, Heading, Text, Button, Flex, Grid } from '@chakra-ui/react';
import './style.css';

const CustomPageWithGrid = () => {
  const faqs = [
    {
      question: 'What is Lorem Ipsum?',
      answer: 'Lorem Ipsum is simply dummy text of the printing and typesetting industry.'
    },
    {
      question: 'Why do we use it?',
      answer: 'It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout.'
    },
    {
      question: 'Where does it come from?',
      answer: 'Contrary to popular belief, Lorem Ipsum is not simply random text.'
    },
    {
      question: 'Where can I get some?',
      answer: 'There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form.'
    },
    {
      question: 'Why does it matter?',
      answer: 'If you are going to use a passage of Lorem Ipsum, you need to be sure there isn\'t anything embarrassing hidden in the middle of text.'
    },
    {
      question: 'Is it safe to use?',
      answer: 'Yes, Lorem Ipsum is safe to use for any purpose.'
    }
    // Add more FAQ items as needed
  ];

  return (
    <Box minH="100vh" py={20} px={8} className="grid-background">
      <Flex align="center" justify="center" textAlign={{ base: 'center' }}>
        <Box maxW="600px" mr={{ md: 8 }}>
          <Heading align="center" as="h1" size="2xl" mb={4} color="gray.700" lineHeight={1.2}>
            Personalized <span className="highlight">Mentorship</span> & <span className="highlight">Guidance</span> From Industry Experts
          </Heading>
          <Text align="center" fontSize="xl" color="gray.700" mb={8}>
            Fueling innovation and opportunity by empowering the next generation of diverse tech leaders with mentorship and access to cutting-edge resources and educational opportunities.
          </Text>
          <Button align="center" colorScheme="blue" size="lg">
            Registration Closed
          </Button>
        </Box>
      </Flex>

      {/* Section with a different background */}
      <Box className="no-background" bg="white" color="gray.700" mt={8} p={8} rounded="md">
        <Heading as="h1" size="xl" mb="4" textAlign="center">
          FAQ Section
        </Heading>
        <Text mb="8" textAlign="center">
          Welcome to our FAQ section. Here you can find answers to commonly asked questions.
        </Text>
        <Grid templateColumns="repeat(3, 1fr)" gap="4">
          {faqs.map((faq, index) => (
            <Box key={index} p="4" borderWidth="1px" borderRadius="md">
              <Text fontWeight="bold">{faq.question}</Text>
              <Text mt="2">{faq.answer}</Text>
            </Box>
          ))}
        </Grid>
      </Box>
    </Box>
  );
};

export default CustomPageWithGrid;
