import React from 'react';
import Slider from 'react-slick';
import { Box, Heading, Text } from '@chakra-ui/react';
import './style.css'; // You can define styles for circular images here

const mentors = [
  { name: 'John Doe', designation: 'Lead Developer', imageUrl: '/files/mentors/john.png' },
  { name: 'Jane Smith', designation: 'UX Designer', imageUrl: '/files/mentors/jane.png' },
  // Add more mentors as needed
];

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 3,
  slidesToScroll: 1,
  autoplay: true,
  autoplaySpeed: 3000, // Slide every 3 seconds
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
        infinite: true,
        dots: true
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
        initialSlide: 1
      }
    }
  ]
};

const MentorCarousel = () => {
  return (
    <Box mt="50px" mx="auto" px={{ base: "20px", md: "150px" }}>
      <Heading as="h2" mb="4" size="s">OUR MENTORS</Heading>
      <Slider {...settings}>
        {mentors.map((mentor, index) => (
          <Box key={index} textAlign="center">
            <Box className="mentor-image">
              <img src={mentor.imageUrl} alt={mentor.name} />
            </Box>
            <Heading as="h3" size="md" mt="3">{mentor.name}</Heading>
            <Text fontSize="sm" color="gray.500">{mentor.designation}</Text>
          </Box>
        ))}
      </Slider>
    </Box>
  );
};

export default MentorCarousel;
