// TeamCarousel.js
import React from 'react';
import { Box, Flex, Heading, Avatar, Text, Link, Icon } from '@chakra-ui/react';
import { FaLinkedin } from 'react-icons/fa';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './style.css'; // Your custom CSS for styling

const teamMembers = [
  {
    name: 'Anjali Chaturvedi',
    position: 'Lead',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://www.linkedin.com/in/anjali-chaturvedi'
  },
  {
    name: 'Hasini Kanchetty',
    position: 'Program Team',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://www.linkedin.com/in/hasini-kanchetty'
  },
  {
    name: 'Tasmiya Khan',
    position: 'Technical Team',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://www.linkedin.com/in/tasmiya-khan'
  },
  {
    name: 'Shriya Reddy Vundhyala',
    position: 'Marketing Team',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://www.linkedin.com/in/shriya-reddy-vundhyala'
  },
  {
    name: 'Priyanka Chitikela',
    position: 'Financial Team',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://www.linkedin.com/in/priyanka-chitikela'
  },
  {
    name: 'Selorm Apeletey Mensah',
    position: 'Program Team',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://www.linkedin.com/in/selorm-apeletey-mensah'
  },
  {
    name: 'Sharon Wainaina',
    position: 'Program Team',
    avatar: 'https://bit.ly/broken-link',
    linkedin: 'https://www.linkedin.com/in/sharon-wainaina'
  },
  // Add more team members here
];

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 3,
  slidesToScroll: 1,
  autoplay: true,
  autoplaySpeed: 3000, // Slide every 3 seconds
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      }
    }
  ]
};

const TeamMember = ({ name, position, avatar, linkedin }) => (
  <Box className="team-member" position="relative" p="4" textAlign="center">
    <Avatar size="xl" src={avatar} mb="4" />
    <Box className="member-info" position="absolute" top="50%" left="50%" transform="translate(-50%, -50%)" textAlign="center" opacity="0" transition="opacity 0.3s ease-in-out">
      <Heading as="h3" size="md" mb="2">{name}</Heading>
      <Text fontSize="sm" color="white" mb="2">{position}</Text>
      <Link href={linkedin} isExternal>
        <Icon as={FaLinkedin} boxSize="6" color="white" />
      </Link>
    </Box>
  </Box>
);

const TeamCarousel = () => {
  return (
    <Box mx="auto" px={{ base: "20px", md: "50px" }} py="10">
      <Heading size="lg" as="h2" mb="4" textAlign="center">Meet The Team</Heading>
      <Slider {...settings}>
        {teamMembers.map((member, index) => (
          <Box key={index} p="2">
            <TeamMember
              name={member.name}
              position={member.position}
              avatar={member.avatar}
              linkedin={member.linkedin}
            />
          </Box>
        ))}
      </Slider>
    </Box>
  );
};

export default TeamCarousel;
