import React, { useEffect } from 'react';
import { Box, Heading, Text, VStack, useMediaQuery } from '@chakra-ui/react';
import { Controller, Scene } from 'react-scrollmagic';
import { Tween, Timeline } from 'react-gsap';

const SectionSlides = () => {
  const [isLargerThan768] = useMediaQuery("(min-width: 768px)");

  return (
    <Controller>
      <Scene
        duration="200%"
        pin
        triggerHook="onLeave"
      >
        {(progress) => (
          <Timeline totalProgress={progress} paused>
            <VStack spacing={8} p={4}>
              <Tween
                from={{ x: '-100%' }}
                to={{ x: '0%' }}
                ease="Power1.easeOut"
              >
                <Box
                  bg="teal.500"
                  p={8}
                  rounded="md"
                  shadow="md"
                  w={isLargerThan768 ? "50%" : "90%"}
                >
                  <Heading as="h2" size="lg" mb={4} color="white">
                    Section 1
                  </Heading>
                  <Text color="white">
                    This is the first section. It will slide in as you scroll down.
                  </Text>
                </Box>
              </Tween>

              <Tween
                from={{ x: '-100%' }}
                to={{ x: '0%' }}
                ease="Power1.easeOut"
              >
                <Box
                  bg="blue.500"
                  p={8}
                  rounded="md"
                  shadow="md"
                  w={isLargerThan768 ? "50%" : "90%"}
                >
                  <Heading as="h2" size="lg" mb={4} color="white">
                    Section 2
                  </Heading>
                  <Text color="white">
                    This is the second section. It will slide in as you scroll down.
                  </Text>
                </Box>
              </Tween>

              <Tween
                from={{ x: '-100%' }}
                to={{ x: '0%' }}
                ease="Power1.easeOut"
              >
                <Box
                  bg="purple.500"
                  p={8}
                  rounded="md"
                  shadow="md"
                  w={isLargerThan768 ? "50%" : "90%"}
                >
                  <Heading as="h2" size="lg" mb={4} color="white">
                    Section 3
                  </Heading>
                  <Text color="white">
                    This is the third section. It will slide in as you scroll down.
                  </Text>
                </Box>
              </Tween>
            </VStack>
          </Timeline>
        )}
      </Scene>
    </Controller>
  );
};

export default SectionSlides;
