// Schedule.jsx
import React from 'react';
import {
  Box,
  Container,
  Heading,
  Text,
  VStack,
  Divider,
  ListItem,
  UnorderedList,
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  useColorModeValue,
} from '@chakra-ui/react';

const schedule = [
  {
    date: '9 June',
    title: 'Induction + Team Building',
    activities: [
      'Introduction and ice-breaking activities',
      'Team-building exercises to foster collaboration',
    ],
  },
  {
    date: '16 June',
    title: 'Familiarize with Git, GitHub, Figma',
    activities: [
      'Introduction to version control using Git and GitHub',
      'Overview of Figma for design collaboration',
      'Task: Complete a beginner\'s tutorial on GitHub and create a sample project in Figma',
    ],
  },
  {
    date: '23 June',
    title: 'Researching Projects and Domains',
    activities: [
      'Strategies for effective project and domain research',
      'Task: Conduct a literature review on your domain of interest',
    ],
  },
  {
    date: '30 June',
    title: 'Group Formation and UX/UI Session',
    activities: [
      'Form groups based on domain interests',
      'Session on wireframes, UX/UI principles',
      'Discuss potential projects and team-building within groups',
      'Task: Draft the first version of your project wireframe',
    ],
  },
  {
    date: '7 July',
    title: 'Product-Market Fit and Product Development',
    activities: [
      'Discussion on product-market fit and key product development considerations',
      'Task: Identify target users and create a preliminary product-market fit analysis',
    ],
  },
  {
    date: '14 July',
    title: 'Meeting with Development Mentor',
    activities: [
      'Initial meeting with a development mentor',
      'Task: Begin coding your project',
    ],
  },
  {
    date: '21 July',
    title: 'Mentor Session + Resume Building',
    activities: [
      'Continue meeting with development mentor',
      'Resume building workshop',
      'Task: Progress in coding and review your resume',
    ],
  },
  {
    date: '28 July',
    title: 'Mentor Session + LinkedIn Building',
    activities: [
      'Further guidance from development mentor',
      'LinkedIn profile optimization session',
      'Task: Continue coding and update your LinkedIn profile',
    ],
  },
  {
    date: '4 August',
    title: 'Finalizing Project and Deployment',
    activities: [
      'Final mentor meeting to finalize the project and plan deployment',
      'Start working on project presentation',
      'Task: Prepare for project deployment and begin creating your presentation',
    ],
  },
  {
    date: '11 August',
    title: 'Project Showcasing',
    activities: [
      'Showcase completed projects',
      'Presentations and feedback session',
      'Celebrate achievements and wrap-up',
    ],
  },
];

const Schedule = () => {
  const textColor = useColorModeValue('gray.700', 'gray.300');

  return (
    <Container maxW="container.md" py={10}>
      <Heading as="h1" mb={8} color={textColor}>
        Schedule
      </Heading>
      <Accordion allowToggle>
        {schedule.map((event, index) => (
          <AccordionItem key={index}>
            <AccordionButton>
              <Box flex="1" textAlign="left" color={textColor}>
                <Heading fontSize="xl">{`${event.date}: ${event.title}`}</Heading>
              </Box>
              <AccordionIcon />
            </AccordionButton>
            <AccordionPanel pb={4}>
              <UnorderedList mt={4} spacing={2} color={textColor}>
                {event.activities.map((activity, i) => (
                  <ListItem key={i}>{activity}</ListItem>
                ))}
              </UnorderedList>
            </AccordionPanel>
          </AccordionItem>
        ))}
      </Accordion>
    </Container>
  );
};

export default Schedule;
