import React, { useEffect } from 'react';
import { Box } from '@chakra-ui/react';

const ChapterApplicationForm = () => {
  useEffect(() => {
    const loadTallyEmbeds = () => {
      if (typeof Tally !== 'undefined') {
        Tally.loadEmbeds();
      } else {
        document.querySelectorAll('iframe[data-tally-src]:not([src])').forEach((iframe) => {
          iframe.src = iframe.dataset.tallySrc;
        });
      }
    };

    if (!window.Tally) {
      const script = document.createElement('script');
      script.src = 'https://tally.so/widgets/embed.js';
      script.onload = loadTallyEmbeds;
      script.onerror = loadTallyEmbeds;
      document.body.appendChild(script);
    } else {
      loadTallyEmbeds();
    }
  }, []);

  return (
    <Box
      boxShadow="none"
      bg="white"
      p="4"
      borderRadius="md"
    >
      <iframe
        data-tally-src="https://tally.so/embed/mOPMR7?alignLeft=1&hideTitle=1&transparentBackground=1&dynamicHeight=1"
        loading="lazy"
        width="100%"
        height="1"
        frameBorder="0"
        marginHeight="0"
        marginWidth="0"
        title="Nodebrew Chapter Application"
      ></iframe>
    </Box>
  );
}

export default ChapterApplicationForm;
