import React from 'react';
import { Flex, Box, Heading, Text, Button } from '@chakra-ui/react';
import CountdownTimer from './Countdown';

const ImageTextButtonLayout = () => {
  return (
    <Box  maxW="800px"  bg="#F5F5F5" color="gray.700">
    <Flex mx="auto" align="center">
      {/* Image Section */}
      <Box flex="1" mt={10}>
        <img
          src="files/images/main.png"
          alt="Placeholder"
          style={{ maxWidth: '100%', height: 'auto' }}
        />
      </Box>

      {/* Text and Button Section */}
      <Box flex="1" mt={10} ml={10}>
        <Heading as="h2" size="lg" mb={4}>
          Brew Bridge
        </Heading>
        <Text fontSize="lg" mb={6}>
           Mentoring program to empower the next-generation of coders in minority with resources and educational opportunities
        </Text>
        <Button bg="teal.400" size="md" _hover={{ transform: "scale(1.05)" }}>
          Register
        </Button>
      </Box>
    </Flex>
    < CountdownTimer />
    </Box>
  );
};

export default ImageTextButtonLayout;
