// TeamCarousel.js
import React from 'react';
import { Box, Flex, Heading, Avatar, Text } from '@chakra-ui/react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './style.css'; // Your custom CSS for styling

const teamMembers = [
  {
    name: 'Anjali Chaturvedi',
    position: 'Lead',
    avatar: 'https://bit.ly/broken-link',
  },
  {
    name: 'Hasini Kanchetty',
    position: 'Program Team',
    avatar: 'https://bit.ly/broken-link',
  },
  {
    name: 'Tasmiya Khan',
    position: 'Technical Team',
    avatar: 'https://bit.ly/broken-link',
  },
  {
    name: 'Shriya Reddy Vundhyala',
    position: 'Marketing Team',
    avatar: 'https://bit.ly/broken-link',
  },
  {
    name: 'Priyanka Chitikela',
    position: 'Financial Team',
    avatar: 'https://bit.ly/broken-link',
  },
  {
    name: 'Selorm Apeletey Mensah',
    position: 'Program Team',
    avatar: 'https://bit.ly/broken-link',
  },
  {
    name: 'Sharon Wainaina',
    position: 'Program Team',
    avatar: 'https://bit.ly/broken-link',
  },
  // Add more team members here
];

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 3,
  slidesToScroll: 1,
  autoplay: true,
  autoplaySpeed: 3000, // Slide every 3 seconds
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      }
    }
  ]
};

const TeamMember = ({ name, position, avatar }) => (
  <Flex direction="column" align="center" p="4" textAlign="center">
    <Avatar size="2xl" src={avatar} mb="4" />
    <Box>
      <Heading as="h3" size="md" mb="2">{name}</Heading>
      <Text fontSize="sm" color="gray.800">{position}</Text>
    </Box>
  </Flex>
);

const TeamCarousel = () => {
  return (
    <Box mx="auto" px={{ base: "20px", md: "50px" }} py="10">
      <Heading size="lg" as="h2" mb="4" textAlign="center">Meet The Team</Heading>
      <Slider {...settings}>
        {teamMembers.map((member, index) => (
          <Box key={index} p="2">
            <TeamMember
              name={member.name}
              position={member.position}
              avatar={member.avatar}
            />
          </Box>
        ))}
      </Slider>
    </Box>
  );
};

export default TeamCarousel;
