import React from 'react';
import Slider from 'react-slick';
import './style.css'; // You can define styles for circular images here

const mentors = [
  { name: 'John Doe', designation: 'Lead Developer', image: 'mentor1.jpg' },
  { name: 'Jane Smith', designation: 'UX Designer', image: 'mentor2.jpg' },
  // Add more mentors as needed
];

const settings = {
  dots: true,
  infinite: true,
  autoplay: true,
  autoplaySpeed: 3000, // Adjust autoplay speed (milliseconds)
  slidesToShow: 1,
  slidesToScroll: 1,
  centerMode: true,
  centerPadding: '0',
  variableWidth: true,
  responsive: [
    {
      breakpoint: 768,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
        variableWidth: false,
        centerMode: false,
      },
    },
  ],
};

const Carousel = () => {
  return (
    <div className="carousel-container">
      <Slider {...settings}>
        {mentors.map((mentor, index) => (
          <div key={index}>
            <div className="mentor-item">
              <div className="mentor-image">
                <img src={mentor.image} alt={mentor.name} />
              </div>
              <div className="mentor-details">
                <h3>{mentor.name}</h3>
                <p>{mentor.designation}</p>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Carousel;
