import React from 'react';
import { Box, Heading, Text, Button, Flex } from '@chakra-ui/react';
import './style.css';

const CustomPageWithGrid = () => {
  return (
    <Box className="grid-background"  minH="100vh" py={20} px={8}>
      <Flex 
        direction={{ base: 'column', md: 'row' }}
        align="center"
        justify="center"
        textAlign={{ base: 'center', md: 'left' }}
      >
        <Box maxW="600px" mr={{ md: 8 }}>
          <Heading align="center" as="h1" size="2xl" mb={4} color="gray.600">
            Personalized <span className="highlight">Mentorship</span> Every Step of the Way!
          </Heading>
          <Text fontSize="xl" color="white" mb={8}>
            Connect with experienced buddies for guidance and mock interviews, empowering you to excel in your academic and professional endeavors.
          </Text>
          <Button colorScheme="blue" size="lg">
            Browse all buddies
          </Button>
        </Box>
      </Flex>
    </Box>
  );
};

export default CustomPageWithGrid;
