import React from 'react';
import { Box, Heading, Text, VStack, Flex } from '@chakra-ui/react';
import { VictoryPie } from 'victory';

const HackBrew = () => {
  return (
    <Box id="hack-brew" py={10} px={5} bg="#F5F5F5">
      <VStack spacing={6} align="start" maxW="800px" mx="auto" color="gray.800">
        <Heading as="h2" size="xl" mb={4}>
          Hack Brew: Promoting Inclusivity in Tech
        </Heading>
        <Text fontSize="lg" mb={4}>
          Hack Brew is a pioneering hackathon dedicated to fostering inclusivity
          within the tech community. It provides a platform for participants to
          innovate, collaborate, and showcase their talents.
        </Text>

        {/* Participants Pie Chart */}
        <Flex justify="center">
          <Box textAlign="center" mr={8}>
            <Heading as="h3" size="lg" mb="4">
              Participants Distribution
            </Heading>
            <VictoryPie
              data={[
                { x: 'Women', y: 70 },
                { x: 'Men', y: 30 },
              ]}
              colorScale={['teal', 'orange']}
              innerRadius={100}
              labelRadius={120}
              style={{ labels: { fill: 'black', fontSize: 14, fontWeight: 'bold' } }}
            />
            <Text mt="4" fontSize="lg">
              Gender Distribution
            </Text>
          </Box>

          {/* Projects Pie Chart */}
          <Box textAlign="center">
            <Heading as="h3" size="lg" mb="4">
              Projects Distribution
            </Heading>
            <VictoryPie
              data={[
                { x: 'Tech Solutions', y: 60 },
                { x: 'Social Impact', y: 20 },
                { x: 'Creative Ideas', y: 10 },
                { x: 'Others', y: 10 },
              ]}
              colorScale={['blue', 'green', 'purple', 'gray']}
              innerRadius={100}
              labelRadius={120}
              style={{ labels: { fill: 'black', fontSize: 14, fontWeight: 'bold' } }}
            />
            <Text mt="4" fontSize="lg">
              Project Categories
            </Text>
          </Box>
        </Flex>

        <Text fontWeight="bold" fontSize="lg">
          Participant Satisfaction:
        </Text>
        <Text fontSize="lg" mb={4}>
          98% of participants would love to come back and recommend to a friend.
        </Text>
        <Text fontSize="lg" mt={6}>
          Join us in the next Hack Brew and be a part of a vibrant community
          driving innovation and inclusivity in technology!
        </Text>
      </VStack>
    </Box>
  );
};

export default HackBrew;
