import React, { useRef, useEffect } from 'react';
import { Box, Flex, Heading, Text } from '@chakra-ui/react';

const CarouselOnScroll = () => {
  const carouselRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const carousel = carouselRef.current;
      if (!carousel) return;

      const items = carousel.children;
      const scrollPosition = window.scrollY;

      items.forEach((item, index) => {
        const topOffset = item.offsetTop;
        const height = item.offsetHeight;

        // Determine when to apply active class based on scroll position
        if (scrollPosition >= topOffset && scrollPosition < topOffset + height) {
          item.classList.add('active');
        } else {
          item.classList.remove('active');
        }
      });
    };

    // Add scroll event listener
    window.addEventListener('scroll', handleScroll);
    // Clean up the listener on unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Box className="carousel-container">
      <Flex className="carousel" ref={carouselRef}>
        <div className="carousel-item">
          <Heading as="h2" size="lg">Slide 1</Heading>
          <Text>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</Text>
        </div>
        <div className="carousel-item">
          <Heading as="h2" size="lg">Slide 2</Heading>
          <Text>Integer feugiat lacus eget justo finibus, sed fringilla nisl consequat.</Text>
        </div>
        <div className="carousel-item">
          <Heading as="h2" size="lg">Slide 3</Heading>
          <Text>Suspendisse potenti. Sed volutpat libero id velit convallis, id lacinia metus suscipit.</Text>
        </div>
      </Flex>
    </Box>
  );
};

export default CarouselOnScroll;
