// MentorCarousel.js
import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './style.css'; // Your custom CSS for styling

const mentors = [
  { name: 'John Doe', designation: 'Lead Developer', imageUrl: '/files/sponsor/balsamiq.png' },
  { name: 'Jane Smith', designation: 'UX Designer', imageUrl: '/files/mentors/jane.png' },
  // Add more mentors as needed
];

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 3,
  slidesToScroll: 1,
  autoplay: true,
  autoplaySpeed: 3000, // Slide every 3 seconds
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
        infinite: true,
        dots: true
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
        initialSlide: 1
      }
    }
  ]
};

const MentorCarousel = () => {
  return (
    <div className="mentor-carousel">
      <Slider {...settings}>
        {mentors.map((mentor, index) => (
          <div key={index} className="mentor-item">
            <div className="mentor-image">
              <img src={mentor.imageUrl} alt={mentor.name} />
            </div>
            <div className="mentor-details">
              <h3>{mentor.name}</h3>
              <p>{mentor.designation}</p>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default MentorCarousel;
